const CampoTexto = ( props) =>{

useState()

  let valor = '' ;


   const aoDigitado =(evento) =>{
    valor = evento.target.value;
    console.log(valor);

   }

    return(
        
            <div  className="campo-texto">

                <label>
                  
                  {props.label}
                </label>
                <input value={valor} onChange={aoDigitado} required={props.campoObrigatorio}   placeholder= {props.placeholder}/>
                

            </div>
     
    )

}

export default  CampoTexto;
