import CampoTexto from '../CampoTexto'
import { ListaSuspensa } from '../ListaSuspensa'


import './Formulario.css'

export const Formulario = () => {

    const times = ['Programação', 'Front-End', 'Data Science', 'Devops', 'UX e Design', 'Mobile', 'Inovação e Gestão'];

    const [] =usesa
    const aoSalvar =(evento) => {
        evento.preventDefault();
        // console.log('aoSalvar');
    }

    return (

        <section className="formulario">

            <form onSubmit={aoSalvar} >
                <h2>Preencha os dados para criar o card do colaborador.</h2>
                <CampoTexto campoObrigatorio={true} label="Nome" placeholder="Digite seu nome" />
                <CampoTexto  campoObrigatorio={true} label="Cargo" placeholder="Digite seu cargo" />
                <CampoTexto label="Imagen" placeholder="http..." />
                <ListaSuspensa  campoObrigatorio={true} label="Time" itens={times} />
                <Botao>
                  Criar card
                    </Botao>
            </form>

        </section>



    )




}
